単純なループによる実装

func CalcDot(a, b []float32) float32 {
    var dot float32 = 0.0
    size := len(a)
    for i := 0; i &lt; size; i++ {
        dot += a[i] * b[i]
    }
    return dot
}

ループアンロールによる実装（N=2）

func CalcDotUnroll2(a, b []float32) float32 {
    const N = 2
    var dot float32 = 0.0

    i := 0
    size := len(a)
    prologue_size := size % N
    for ; i &lt; prologue_size; i++ {
        dot += a[i] * b[i]
    }

    for ; i &lt; size; i += N {
        mul0 := a[i+0] * b[i+0]
        mul1 := a[i+1] * b[i+1]
        dot += mul0 + mul1
    }

    return dot
}

ループアンロールによる実装（N=4）

func CalcDotUnroll4(a, b []float32) float32 {
    const N = 4
    var dot float32 = 0.0

    i := 0
    size := len(a)
    prologue_size := size % N
    for ; i &lt; prologue_size; i++ {
        dot += a[i] * b[i]
    }

    for ; i &lt; size; i += N {
        mul0 := a[i+0] * b[i+0]
        mul1 := a[i+1] * b[i+1]
        mul2 := a[i+2] * b[i+2]
        mul3 := a[i+3] * b[i+3]
        dot += mul0 + mul1 + mul2 + mul3
    }

    return dot
}

ループアンロールによる実装（N=8）

func CalcDotUnroll8(a, b []float32) float32 {
    const N = 8
    var dot float32 = 0.0

    i := 0
    size := len(a)
    prologue_size := size % N
    for ; i &lt; prologue_size; i++ {
        dot += a[i] * b[i]
    }

    for ; i &lt; size; i += N {
        mul0 := a[i+0] * b[i+0]
        mul1 := a[i+1] * b[i+1]
        mul2 := a[i+2] * b[i+2]
        mul3 := a[i+3] * b[i+3]
        mul4 := a[i+4] * b[i+4]
        mul5 := a[i+5] * b[i+5]
        mul6 := a[i+6] * b[i+6]
        mul7 := a[i+7] * b[i+7]
        dot += mul0 + mul1 + mul2 + mul3 + mul4 + mul5 + mul6 + mul7 
    }

    return dot
}

Gonumによる実装（blas）

import (
    "gonum.org/v1/gonum/blas/blas32"
)

func CalcDotByGonumBlas(a, b []float32) float32 {
    size := len(a)
    av := blas32.Vector{
        N: size,
        Inc:  1,
        Data: a,
    }
    bv := blas32.Vector{
        N: size,
        Inc:  1,
        Data: b,
    }
    return blas32.Dot(av, bv)
}

Weaviateによる実装（asm）

import (
    "github.com/semi-technologies/weaviate/adapters/repos/db/vector/hnsw/distancer/asm"
)

func CalcDotByWeaviateAsm(a, b []float32) float32 {
    return asm.Dot(a, b)
}

実体を表現する構造体をunion(enum)でまとめ、関数の呼び出しの際に自身をswitchで分岐することで呼び出す関数を判別します。 この際switchの対象にinline elseを指定することで記述を簡略化することができます。
以下にunion(enum)による方法のサンプルを示します。
const std = @import("std");
const stdout = std.io.getStdOut().writer();

pub const Animal = union(enum) {
    const Self = @This();
    dog: Dog,
    cat: Cat,

    pub fn bark(self: Self) []const u8 {
        return switch (self) {
            inline else =&gt; |n| n.bark(),
        };
    }
};

pub const Dog = struct {
    const Self = @This();

    buffer: [64]u8,
    len: usize,

    pub fn init(name: []const u8) !Animal {
        const dog = blk: {
            var dog = Self{
                .buffer = undefined,
                .len = 0,
            };

            const s = try std.fmt.bufPrint(&dog.buffer, "{s} - {s}", .{ name, "wan wan" });
            dog.len = s.len;
            break :blk dog;
        };

        return .{ .dog = dog };
    }

    pub fn bark(self: Self) []const u8 {
        return self.buffer[0..self.len];
    }
};

pub const Cat = struct {
    const Self = @This();

    buffer: [64]u8,
    len: usize,

    pub fn init(name: []const u8) !Animal {
        const cat = blk: {
            var cat = Self{
                .buffer = undefined,
                .len = 0,
            };

            const s = try std.fmt.bufPrint(&cat.buffer, "{s} - {s}", .{ name, "nyan nyan" });
            cat.len = s.len;
            break :blk cat;
        };

        return .{ .cat = cat };
    }

    pub fn bark(self: Self) []const u8 {
        return self.buffer[0..self.len];
    }
};

test "tagged_union" {
    const animals = [_]Animal{
        try Dog.init("pochi"),
        try Cat.init("mike"),
    };

    try std.testing.expectEqualStrings("pochi - wan wan", animals[0].bark());
    try std.testing.expectEqualStrings("mike - nyan nyan", animals[1].bark());
}

