func CalcDot(a, b []float64, size int) float64 {
    dot := 0.0
    for i := 0; i &lt; size; i++ {
        dot += a[i] * b[i]
    }
    return dot
}

ループアンロールによる実装（N=2）

func CalcDotUnroll2(a, b []float64, size int) float64 {
    const N = 2
    dot := 0.0

    i := 0
    for ; i &lt; size % N; i++ {
        dot += a[i] * b[i]
    }

    for ; i &lt; size; i += N {
        mul0 := a[i+0] * b[i+0]
        mul1 := a[i+1] * b[i+1]
        dot += mul0 + mul1
    }

    return dot
}

ループアンロールによる実装（N=4）

func CalcDotUnroll4(a, b []float64, size int) float64 {
    const N = 4
    dot := 0.0

    i := 0
    for ; i &lt; size % N; i++ {
        dot += a[i] * b[i]
    }

    for ; i &lt; size; i += N {
        mul0 := a[i+0] * b[i+0]
        mul1 := a[i+1] * b[i+1]
        mul2 := a[i+2] * b[i+2]
        mul3 := a[i+3] * b[i+3]
        dot += mul0 + mul1 + mul2 + mul3
    }

    return dot
}

ループアンロールによる実装（N=8）

func CalcDotUnroll8(a, b []float64, size int) float64 {
    const N = 8
    dot := 0.0

    i := 0
    for ; i &lt; size % N; i++ {
        dot += a[i] * b[i]
    }

    for ; i &lt; size; i += N {
        mul0 := a[i+0] * b[i+0]
        mul1 := a[i+1] * b[i+1]
        mul2 := a[i+2] * b[i+2]
        mul3 := a[i+3] * b[i+3]
        mul4 := a[i+4] * b[i+4]
        mul5 := a[i+5] * b[i+5]
        mul6 := a[i+6] * b[i+6]
        mul7 := a[i+7] * b[i+7]
        dot += mul0 + mul1 + mul2 + mul3 + mul4 + mul5 + mul6 + mul7 
    }

    return dot
}

